import react from "react";

class FlippyCard extends React.Component {
  render() {
    const {
      className,
      cardType,
      style,
      elementType,
      animationDuration,
      ...restprops
    } = this.props;
    return React.createElement(
      elementType || "div",
      {
        className: `flippy-card flippy-${cardType} ${className || ""}`,
        ...rest,
        style: {
          ...(style || {}),
          ...{ transitionDuration: `${animationDuration / 1000}s` },
        },
      },
      this.props.children
    );
  }
}

export default FrontSide = ({
  isFlipped,
  style,
  animationDuration,
  ...restprops
}) => (
  <FlippyCard
    {...props}
    style={{
      ...(style || {}),
    }}
    animationDuration={animationDuration}
    cardType='front'
  />
);

export const BackSide = ({ isFlipped, style, ...restprops }) => (
  <FlippyCard
    {...props}
    style={{
      ...(style || {}),
    }}
    cardType='back'
  />
);

FlippyCard.defaultProps = {
  animationDuration: 600,
};
