import React, { Component, Button, ButtonGroup } from "react";

class category extends Component {
  state = {
    category: "",
  };

  handleChange = (event) => {
    this.setState({ category: event.target.value });
    console.log(event.target.value);
  };

  handleSubmit = (event) => {
    console.log("Hi there");
    event.preventDefault();
    this.props.history.push(`/category/${this.state.category}`);
  };

  render() {
    return (
      <form
        className='buttonGroup'
        onChange={this.handleChange}
        onSubmit={this.handleSubmit}
        style={{
          display: "flex",
          flexDirection: "row",
          marginRight: "25px",
          marginBottom: "5px",
        }}
        size='lg'>
        <button size='lg' value='Entertainment' type='submit'>
          Entertainment
        </button>
        <button size='lg' value='Politics' type='submit'>
          Politics
        </button>
        <button size='lg' value='Sports' type='submit'>
          Sports
        </button>
        <button size='lg' value='LifeStyle' type='submit'>
          LifeStyle
        </button>
        <button size='lg' value='Business' type='submit'>
          Business
        </button>
      </form>
    );
  }
}

export default category;
