import React, { Component, Button, ButtonGroup } from "react";

class category extends Component {
  state = {
    category: "",
  };

  handleChange = (event) => {
    this.setState({ category: event.target.value });
    console;
  };

  handleSubmit = (event) => {
    console.log("Hi there");
    event.preventDefault();
    this.props.history.push(`/category/${this.state.category}`);
  };

  render() {
    return (
      <form
        className='buttonGroup'
        style={{
          display: "flex",
          flexDirection: "row",
          marginRight: "25px",
          marginBottom: "5px",
        }}
        size='lg'>
        <button size='lg' value='Entertainment'>
          Entertainment
        </button>
        <button size='lg'>Politics</button>
        <button size='lg'>Sports</button>
        <button size='lg'>LifeStyle</button>
        <button size='lg'>Business</button>
      </form>
    );
  }
}

export default category;
